import React from 'react';
import TeacherItem from './ListItems/TeacherItem'
import api from '../services/api';

class Periods extends React.Component {


  //nessa requisição se define a pagina para exibir
  getAllTeachers = (page = 1) => {
    api.get('/allTeachers').then((resp)=>{
      console.log(resp)
        this.setState({periods:resp.data});
      }
    )
  }
  componentDidMount(){
    this.getAllTeachers();

  }
  state = {
    periods:[],
    newTeacherName:''
  }
  onTypeName = (event) => {
    this.setState({newTeacherName:event.target.value})
    console.log(this.state)
  }
  saveTeacherHandler = (event) => {
    event.preventDefault();
    api.post('/periods',{
      name:this.state.newTeacherName
    }).then(
      ()=>{
        this.setState({newTeacherName:''});
        this.getAllTeachers(); 
      }
    )
  }


  render() {
    console.log(this.state)
    return <div>
      <form>
        <input placeholder="Hora inicial" name="name" onChange={this.onTypeName} value={this.state.newTeacherName}></input>
        <button onClick={this.saveTeacherHandler}>Cadastrar</button>
      </form>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Hora inicial</th>
            <th scope="col">Hora final</th>
            <th scope="col">Ações</th>
          </tr>
        </thead>
        <tbody>
        {
        this.state.periods.map((period)=>(
          <TeacherItem key={period._id} name={period.name} id={period._id}/>

        ))
      }
        </tbody>
      </table>
    </div>;
  }
}

export default Periods;

