import React from 'react';
import api from '../../services/api'
import StudentItemClass from '../ListItems/StudentItemClass';



class ClassTeam extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      id_turma:this.props.match.params.id,
      turmaDetail:{},
      allStudents:[],
      studentsOfTheClassTeam:[],
      totalStudents:'',
      lastStudent:'',
    }
  }

  getStudents = () =>{
    api.get('/allStudents').then((resp)=>{
      this.setState({allStudents:resp.data})
    })
  }
    getStudentsOfClassTeam = () =>{
        api.get('/classTeams/'+this.state.id_turma).then((resp)=>{
          const idStudents = resp.data.students;

          let studentsDetails = []; 
          idStudents.map((idStu)=>{
            api.get('/students/'+idStu).then((student)=>{
              studentsDetails.push(student.data);
              this.setState({studentsOfTheClassTeam:studentsDetails})
            })
          });
        })
      }
    enrollNewStudent = async (event) => {
     api.post('/classTeams/addStudent', {
          student_id:event.target.value, classTeam_id:this.state.id_turma 
        })
        window.location.reload();
      }
    componentDidMount(){
      this.getStudents();
      this.getStudentsOfClassTeam();
    }

    deleteStudent = () =>{
      api.post('/classTeams/removeStudent', {student_id:1,classTeam_id:this.state.id_turma } )
      window.location.reload();
  }
    render (){
      //renderiza o cadastro apenas se o numero de alunos for menor ou igual a 12
        return <div>
              <h2>Alunos da turma {this.state.totalStudents}</h2>
                    <br/>
                    <div>
                        <p><p className="bg-success">Total de alunos: {
                          this.state.studentsOfTheClassTeam.length
                        }</p>
                        Adicionar alunos na turma 
                        </p>
                      <select onChange={this.enrollNewStudent}>
                        <option></option>
                      {
                        this.state.allStudents.map((student)=>(
                        <option value={student._id}>{student.name}</option>
                        ))
                      }
                      </select>
                    </div>

                  <table className="table">
                  <thead>
                    <tr>
                      <th scope="col">ID</th>
                      <th scope="col">Nome Turma</th>
                      <th scope="col">Ações</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      this.state.studentsOfTheClassTeam.map((student)=>(
                      <tr>
                        <th scope="row">{student._id}</th>
                        <td>{student.name}</td>
                        <td>
                            <button className="btn btn-danger" onClick={this.deleteStudent}>Remover da turma</button>
                        </td>
                    </tr>
                      ))
                    }
                  </tbody>
                </table> 
                </div>  

    }
}

export default ClassTeam
